import React from 'react';

export function LoadingSpinner({ size = 'default', fullScreen = false }) {
  // تحديد حجم العنصر بناءً على الخيار المحدد
  const sizeClasses = {
    small: 'h-6 w-6 border-2',
    default: 'h-10 w-10 border-2',
    large: 'h-16 w-16 border-4',
  };

  // إذا كان fullScreen صحيحًا، سيتم عرض العنصر في وسط الشاشة
  if (fullScreen) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center space-y-4">
          <div className={`animate-spin rounded-full border-t-primary border-b-primary border-r-transparent border-l-transparent mx-auto ${sizeClasses[size] || sizeClasses.default}`}></div>
          <h2 className="text-xl font-semibold">جاري التحميل...</h2>
          <p className="text-muted-foreground">يرجى الانتظار بينما نقوم بتحميل البيانات</p>
        </div>
      </div>
    );
  }

  // إذا لم يكن fullScreen صحيحًا، سيتم عرض العنصر فقط
  return (
    <div className={`animate-spin rounded-full border-t-primary border-b-primary border-r-transparent border-l-transparent ${sizeClasses[size] || sizeClasses.default}`}></div>
  );
}

export function LoadingPage() {
  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col items-center justify-center h-[70vh]">
          <LoadingSpinner size="large" />
          <h2 className="text-xl font-semibold mt-4">جاري التحميل...</h2>
          <p className="text-muted-foreground">يرجى الانتظار بينما نقوم بتحميل البيانات</p>
        </div>
      </div>
    </div>
  );
}

export function LoadingCards({ count = 4, columns = 4 }) {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {[...Array(count)].map((_, i) => (
        <div key={i} className="animate-pulse">
          <div className="bg-muted h-48 rounded-md mb-4"></div>
          <div className="bg-muted h-4 rounded mb-2"></div>
          <div className="bg-muted h-4 rounded w-2/3"></div>
        </div>
      ))}
    </div>
  );
}

export function LoadingItems({ count = 3 }) {
  return (
    <div className="animate-pulse space-y-4">
      {[...Array(count)].map((_, i) => (
        <div key={i} className="bg-card p-6 rounded-lg">
          <div className="flex gap-4">
            <div className="bg-muted h-20 w-20 rounded"></div>
            <div className="flex-1 space-y-2">
              <div className="bg-muted h-4 rounded w-3/4"></div>
              <div className="bg-muted h-4 rounded w-1/2"></div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
